import React, { useState, useEffect, useRef } from 'react';

const AIAvatar = ({ isThinking = false, isReplying = false }) => {
  const [currentState, setCurrentState] = useState('normal');
  const [showAha, setShowAha] = useState(false);
  const [imagesLoaded, setImagesLoaded] = useState(false);
  const [isTransitioning, setIsTransitioning] = useState(false);
  const timeoutRef = useRef(null);
  const loadingTimeoutRef = useRef(null);

  // È¢ÑÂä†ËΩΩÂõæÁâáÂπ∂ÁõëÂê¨Âä†ËΩΩÂÆåÊàê - Êõ¥ÊøÄËøõÁöÑ‰ºòÂåñ
  useEffect(() => {
    const images = ['/Picture1.png', '/Picture2.png', '/Picturee.png'];
    let loadedCount = 0;
    const imageCache = {};

    const checkAllLoaded = () => {
      loadedCount++;
      if (loadedCount === images.length) {
        setImagesLoaded(true);
        if (loadingTimeoutRef.current) {
          clearTimeout(loadingTimeoutRef.current);
        }
      }
    };

    // ËÆæÁΩÆÊõ¥Áü≠ÁöÑÁ≠âÂæÖÊó∂Èó¥ÔºåËÆ©AIÂΩ¢Ë±°Êõ¥Âø´ÊòæÁ§∫
    loadingTimeoutRef.current = setTimeout(() => {
      setImagesLoaded(true);
    }, 50); // Ëøõ‰∏ÄÊ≠•ÂáèÂ∞ëÂà∞50ms

    // Á´ãÂç≥ÂºÄÂßãÈ¢ÑÂä†ËΩΩÂõæÁâáÔºå‰ΩøÁî®Êõ¥ÊøÄËøõÁöÑÂä†ËΩΩÁ≠ñÁï•
    images.forEach(src => {
      const img = new Image();
      img.onload = () => {
        imageCache[src] = img; // ÁºìÂ≠òÂ∑≤Âä†ËΩΩÁöÑÂõæÁâá
        checkAllLoaded();
      };
      img.onerror = checkAllLoaded; // Âç≥‰ΩøÂä†ËΩΩÂ§±Ë¥•‰πüÁªßÁª≠
      // ËÆæÁΩÆÂõæÁâáÂä†ËΩΩ‰ºòÂÖàÁ∫ß
      img.loading = 'eager'; // Á´ãÂç≥Âä†ËΩΩ
      img.decoding = 'sync'; // ÂêåÊ≠•Ëß£Á†Å
      img.src = src;
    });

    // È¢ùÂ§ñÁöÑÈ¢ÑÂä†ËΩΩÁ≠ñÁï•ÔºöÂàõÂª∫ÈöêËóèÁöÑimgÂÖÉÁ¥†
    images.forEach(src => {
      const hiddenImg = document.createElement('img');
      hiddenImg.style.display = 'none';
      hiddenImg.loading = 'eager';
      hiddenImg.decoding = 'sync';
      hiddenImg.src = src;
      document.body.appendChild(hiddenImg);
    });

    // Ê∏ÖÁêÜÂáΩÊï∞
    return () => {
      if (loadingTimeoutRef.current) {
        clearTimeout(loadingTimeoutRef.current);
      }
    };
  }, []);

  // ÁõëÂê¨Áä∂ÊÄÅÂèòÂåñ - Êõ¥Âø´ÈÄüÁöÑÁä∂ÊÄÅÂàáÊç¢
  useEffect(() => {
    // Ê∏ÖÈô§‰πãÂâçÁöÑÂÆöÊó∂Âô®
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }

    if (isThinking) {
      setCurrentState('thinking');
      setIsTransitioning(false);
    } else if (isReplying) {
      setCurrentState('aha');
      setShowAha(true);
      setIsTransitioning(false);
      // 1ÁßíÂêéÂºÄÂßãÂø´ÈÄüÂàáÊç¢ÂõûÊ≠£Â∏∏Áä∂ÊÄÅ
      timeoutRef.current = setTimeout(() => {
        setShowAha(false);
        setIsTransitioning(true);
        // Ê∑ªÂä†‰∏Ä‰∏™Êõ¥Áü≠ÁöÑËøáÊ∏°Êó∂Èó¥ÔºåËÆ©ÂàáÊç¢Êõ¥Âø´
        setTimeout(() => {
          setCurrentState('normal');
          setIsTransitioning(false);
        }, 150); // ÂáèÂ∞ëÂà∞150ms
      }, 1000); // ÂáèÂ∞ëÂà∞1Áßí
    } else {
      setCurrentState('normal');
      setIsTransitioning(false);
    }

    // Ê∏ÖÁêÜÂáΩÊï∞
    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, [isThinking, isReplying]);

  // Ê†πÊçÆÁä∂ÊÄÅÈÄâÊã©ÂõæÁâá - ‰ΩøÁî®ÁºìÂ≠òÁöÑÂõæÁâá
  const getImageSrc = () => {
    if (currentState === 'thinking') {
      return '/Picture1.png';
    } else if (currentState === 'aha') {
      return '/Picture2.png';
    } else {
      return '/Picturee.png';
    }
  };

  // Ëé∑ÂèñCSSÁ±ªÂêçÔºåÂåÖÂê´Ëá™ÁÑ∂ÂàáÊç¢Âä®Áîª
  const getAvatarClassName = () => {
    if (isTransitioning && currentState === 'normal') {
      return 'ai-avatar normal normal-return';
    }
    return `ai-avatar ${currentState}`;
  };

  // Â¶ÇÊûúÂõæÁâáËøòÊ≤°Âä†ËΩΩÂÆåÊàêÔºåÊòæÁ§∫Êõ¥Âø´ÁöÑÂä†ËΩΩÁä∂ÊÄÅ
  if (!imagesLoaded) {
    return (
      <div style={{ 
        position: 'absolute',
        left: '10px',
        bottom: '10px',
        zIndex: 1000,
        width: '220px',
        height: '280px',
        pointerEvents: 'none',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}>
        <div style={{
          width: '60px',
          height: '60px',
          borderRadius: '50%',
          background: 'linear-gradient(45deg, #22c55e, #16a34a)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          color: 'white',
          fontSize: '24px',
          animation: 'pulse 0.6s ease-in-out infinite', // Ëøõ‰∏ÄÊ≠•Âä†Âø´ËÑâÂÜ≤Âä®Áîª
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)'
        }}>
          AI
        </div>
      </div>
    );
  }

  return (
    <div style={{ 
      position: 'absolute',
      left: '10px',
      bottom: '10px',
      zIndex: 1000,
      width: '220px',
      height: '280px',
      pointerEvents: 'none'
    }}>
      {/* ‰ΩøÁî®PhotoShopÊä†ÂõæÁöÑÂõæÁâá - Êõ¥Âø´ÈÄüÁöÑÂàáÊç¢ÊïàÊûúÔºåÈÅøÂÖçÊ∏êËøõÂºèÂä†ËΩΩ */}
      <div className={getAvatarClassName()} style={{
        position: 'absolute',
        bottom: '0',
        left: '0',
        width: '220px',
        height: '240px'
      }}>
        <div 
          style={{
            width: '100%',
            height: '100%',
            backgroundImage: `url(${getImageSrc()})`,
            backgroundSize: 'contain',
            backgroundPosition: 'center',
            backgroundRepeat: 'no-repeat',
            filter: 'drop-shadow(0 4px 8px rgba(0, 0, 0, 0.1))',
            // Âè™Âú®‰ªéÊÅçÁÑ∂Â§ßÊÇüÂàáÊç¢ÂõûÊ≠£Â∏∏Áä∂ÊÄÅÊó∂Ê∑ªÂä†ËøáÊ∏°ÊïàÊûúÔºå‰ΩÜÊõ¥Âø´ÈÄü
            transition: isTransitioning ? 'opacity 0.15s ease-in-out' : 'none',
            opacity: isTransitioning ? 0.9 : 1,
            // Ê∑ªÂä†Ëøô‰∫õÂ±ûÊÄßÊù•ÈÅøÂÖçÊ∏êËøõÂºèÂä†ËΩΩ
            imageRendering: 'crisp-edges',
            backfaceVisibility: 'hidden',
            transform: 'translateZ(0)', // Âº∫Âà∂Á°¨‰ª∂Âä†ÈÄü
            // Á°Æ‰øùÂõæÁâáÁ´ãÂç≥ÊòæÁ§∫ÔºåÊó†Ê∏êËøõÂºèÂä†ËΩΩ
            objectFit: 'contain',
            objectPosition: 'center'
          }}
        />
      </div>

      {/* ÊÅçÁÑ∂Â§ßÊÇüÁä∂ÊÄÅÁâπÊïà */}
      {showAha && (
        <div className="aha-effects" style={{
          position: 'absolute',
          top: '-5px',
          left: '50%',
          transform: 'translateX(-50%)',
          zIndex: 1001,
          transition: 'opacity 0.15s ease-in-out', // Êõ¥Âø´ÈÄüÁöÑÊ∑°Âá∫
          opacity: isTransitioning ? 0 : 1
        }}>
          <div className="aha-bubble" style={{
            width: '40px',
            height: '40px',
            borderRadius: '50%',
            border: '4px solid #FFD700',
            opacity: 0.9,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            animation: 'ahaGlow 0.2s ease-out' // Êõ¥Âø´ÈÄüÁöÑÁâπÊïà
          }}>
            <span style={{
              color: '#FFD700',
              fontSize: '24px',
              fontWeight: 'bold'
            }}>üí°</span>
          </div>
        </div>
      )}
    </div>
  );
};

export default AIAvatar;
