import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import ReactMarkdown from 'react-markdown';
import rehypeRaw from 'rehype-raw';
import EmojiPicker from 'emoji-picker-react';
import SimpleFrontendRAG from '../utils/simpleFrontendRAG';

const SYSTEM_PROMPT = `‰Ω†ÊòØ‰∏Ä‰∏™‰∏ì‰∏öÁöÑÂ§ßÂ≠¶Âí®ËØ¢Âä©ÊâãÔºå‰∏ìÈó®‰∏∫Â§ßÂ≠¶ÁîüÊèê‰æõÂ≠¶‰π†ÂíåÁîüÊ¥ªÊñπÈù¢ÁöÑÂª∫ËÆÆ„ÄÇËØ∑Ê†πÊçÆÁî®Êà∑ÁöÑÈóÆÈ¢òÊèê‰æõËØ¶ÁªÜ„ÄÅÂÆûÁî®ÁöÑÂõûÁ≠î„ÄÇ`;

const FrontendRAGChat = () => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [ragSystem, setRagSystem] = useState(null);
  const [ragStatus, setRagStatus] = useState('initializing'); // 'initializing', 'ready', 'error'
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    // ÂàùÂßãÂåñÁÆÄÂåñÁâàÂâçÁ´ØRAGÁ≥ªÁªü
    const initRAG = async () => {
      try {
        setRagStatus('initializing');
        const rag = new SimpleFrontendRAG();
        await rag.initialize();
        setRagSystem(rag);
        setRagStatus('ready');
        console.log('‚úÖ ÁÆÄÂåñÁâàÂâçÁ´ØRAGÁ≥ªÁªüÂ∑≤Â∞±Áª™');
      } catch (error) {
        console.error('‚ùå ÁÆÄÂåñÁâàÂâçÁ´ØRAGÁ≥ªÁªüÂàùÂßãÂåñÂ§±Ë¥•:', error);
        setRagStatus('error');
      }
    };

    initRAG();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!inputMessage.trim() || isLoading) return;

    const userMessage = inputMessage.trim();
    setInputMessage('');
    setShowEmojiPicker(false);

    // Ê∑ªÂä†Áî®Êà∑Ê∂àÊÅØ
    setMessages(prev => [...prev, { role: 'user', content: userMessage }]);
    setIsLoading(true);

    try {
      let ragContext = '';
      
      // ‰ΩøÁî®ÁÆÄÂåñÁâàÂâçÁ´ØRAGÁ≥ªÁªüÊü•ËØ¢Áõ∏ÂÖ≥ÊñáÊ°£
      if (ragSystem && ragStatus === 'ready') {
        try {
          const ragResult = await ragSystem.query(userMessage, 3);
          if (ragResult.relevant_docs && ragResult.relevant_docs.length > 0) {
            ragContext = `\n\nÁõ∏ÂÖ≥‰∏ì‰∏öÁü•ËØÜÂ∫ì‰ø°ÊÅØÔºö\n${ragResult.relevant_docs.join('\n\n')}`;
            console.log('ÁÆÄÂåñÁâàÂâçÁ´ØRAGÊü•ËØ¢ÁªìÊûú:', ragResult);
          }
        } catch (ragError) {
          console.log('ÁÆÄÂåñÁâàÂâçÁ´ØRAGÊü•ËØ¢Â§±Ë¥•ÔºåÁªßÁª≠‰ΩøÁî®DeepSeek API');
        }
      }

      // Ë∞ÉÁî®DeepSeek API
      const response = await axios.post('/v1/chat/completions', {
        model: 'deepseek-chat',
        messages: [
          {
            role: 'system',
            content: `${SYSTEM_PROMPT}${ragContext}`
          },
          {
            role: 'user',
            content: userMessage
          }
        ],
        max_tokens: 1000,
        temperature: 0.7
      }, {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer sk-7f5214ed15764dfea0b45c84c6d0c961'
        }
      });

      const assistantMessage = response.data.choices[0].message.content;
      setMessages(prev => [...prev, { role: 'assistant', content: assistantMessage }]);

    } catch (error) {
      console.error('APIË∞ÉÁî®Â§±Ë¥•:', error);
      setMessages(prev => [...prev, { 
        role: 'assistant', 
        content: 'Êä±Ê≠âÔºåÊàëÈÅáÂà∞‰∫Ü‰∏Ä‰∫õÈóÆÈ¢ò„ÄÇËØ∑Á®çÂêéÂÜçËØï„ÄÇ' 
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleEmojiClick = (emojiObject) => {
    setInputMessage(prev => prev + emojiObject.emoji);
  };

  const getRagStatusText = () => {
    switch (ragStatus) {
      case 'initializing':
        return 'üîÑ ÁÆÄÂåñÁâàÂâçÁ´ØRAGÁ≥ªÁªüÂàùÂßãÂåñ‰∏≠...';
      case 'ready':
        return '‚úÖ ÁÆÄÂåñÁâàÂâçÁ´ØRAGÁ≥ªÁªüÂ∑≤Â∞±Áª™';
      case 'error':
        return '‚ùå ÁÆÄÂåñÁâàÂâçÁ´ØRAGÁ≥ªÁªüÂàùÂßãÂåñÂ§±Ë¥•';
      default:
        return '‚è≥ ÁÆÄÂåñÁâàÂâçÁ´ØRAGÁ≥ªÁªüÁä∂ÊÄÅÊú™Áü•';
    }
  };

  return (
    <div className="flex flex-col h-screen bg-gray-50">
      {/* Â§¥ÈÉ® */}
      <div className="bg-white shadow-sm border-b px-6 py-4">
        <h1 className="text-xl font-semibold text-gray-800">AIÊô∫ËÉΩÂí®ËØ¢Âä©Êâã</h1>
        <p className="text-sm text-gray-600 mt-1">Âü∫‰∫éÁÆÄÂåñÁâàÂâçÁ´ØRAGÁ≥ªÁªüÁöÑÊô∫ËÉΩÂØπËØù</p>
        <div className="mt-2 text-xs text-gray-500">
          {getRagStatusText()}
        </div>
      </div>

      {/* Ê∂àÊÅØÂå∫Âüü */}
      <div className="flex-1 overflow-y-auto p-6 space-y-4">
        {messages.length === 0 && (
          <div className="text-center text-gray-500 mt-20">
            <div className="text-6xl mb-4">ü§ñ</div>
            <p className="text-lg">Ê¨¢Ëøé‰ΩøÁî®AIÊô∫ËÉΩÂí®ËØ¢Âä©ÊâãÔºÅ</p>
            <p className="text-sm mt-2">ÊàëÂèØ‰ª•‰∏∫ÊÇ®Êèê‰æõÂ≠¶‰π†ÂíåÁîüÊ¥ªÊñπÈù¢ÁöÑÂª∫ËÆÆ</p>
          </div>
        )}
        
        {messages.map((message, index) => (
          <div
            key={index}
            className={`flex ${message.role === 'user' ? 'justify-end' : 'justify-start'}`}
          >
            <div
              className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${
                message.role === 'user'
                  ? 'bg-blue-500 text-white'
                  : 'bg-white text-gray-800 shadow-sm border'
              }`}
            >
              <ReactMarkdown
                rehypePlugins={[rehypeRaw]}
                className="prose prose-sm max-w-none"
              >
                {message.content}
              </ReactMarkdown>
            </div>
          </div>
        ))}
        
        {isLoading && (
          <div className="flex justify-start">
            <div className="bg-white text-gray-800 shadow-sm border px-4 py-2 rounded-lg">
              <div className="flex items-center space-x-2">
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-500"></div>
                <span className="text-sm">Ê≠£Âú®ÊÄùËÄÉ...</span>
              </div>
            </div>
          </div>
        )}
        
        <div ref={messagesEndRef} />
      </div>

      {/* ËæìÂÖ•Âå∫Âüü */}
      <div className="bg-white border-t p-4">
        <form onSubmit={handleSubmit} className="flex items-center space-x-2">
          <div className="relative flex-1">
            <input
              type="text"
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              placeholder="ËæìÂÖ•ÊÇ®ÁöÑÈóÆÈ¢ò..."
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              disabled={isLoading}
            />
            {showEmojiPicker && (
              <div className="absolute bottom-full right-0 mb-2">
                <EmojiPicker onEmojiClick={handleEmojiClick} />
              </div>
            )}
          </div>
          
          <button
            type="button"
            onClick={() => setShowEmojiPicker(!showEmojiPicker)}
            className="p-2 text-gray-500 hover:text-gray-700"
          >
            üòä
          </button>
          
          <button
            type="submit"
            disabled={!inputMessage.trim() || isLoading}
            className="px-6 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            ÂèëÈÄÅ
          </button>
        </form>
      </div>
    </div>
  );
};

export default FrontendRAGChat;
