import React, { useState, useRef, useEffect } from 'react';
import './AdminPage.css';

const AdminPage = ({ onLogout }) => {
  const [dragActive, setDragActive] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [selectedMonth, setSelectedMonth] = useState(new Date().getMonth() + 1);
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());
  
  // 模拟用户访问数据
  const [userVisits] = useState([
    { id: 1, username: '2024215001', time: '2024-01-15 14:30:25', question: '微电子专业的就业前景如何？' },
    { id: 2, username: '2024215123', time: '2024-01-15 15:20:10', question: 'GPA成绩对就业有什么影响？' },
    { id: 3, username: '2024215999', time: '2024-01-15 16:45:33', question: '如何提高编程能力？' },
    { id: 4, username: '2024215001', time: '2024-01-15 17:10:45', question: '集成电路设计课程难度大吗？' },
    { id: 5, username: '2024215123', time: '2024-01-15 18:25:12', question: '有哪些实习机会推荐？' },
  ]);

  // 模拟访问量数据
  const [visitData] = useState([
    { date: '2024-01-01', visits: 45 },
    { date: '2024-01-02', visits: 52 },
    { date: '2024-01-03', visits: 38 },
    { date: '2024-01-04', visits: 67 },
    { date: '2024-01-05', visits: 73 },
    { date: '2024-01-06', visits: 89 },
    { date: '2024-01-07', visits: 95 },
    { date: '2024-01-08', visits: 82 },
    { date: '2024-01-09', visits: 76 },
    { date: '2024-01-10', visits: 91 },
    { date: '2024-01-11', visits: 88 },
    { date: '2024-01-12', visits: 94 },
    { date: '2024-01-13', visits: 101 },
    { date: '2024-01-14', visits: 87 },
    { date: '2024-01-15', visits: 92 },
  ]);

  const fileInputRef = useRef(null);

  // 处理拖拽事件
  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  // 处理文件上传
  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      handleFiles(e.dataTransfer.files);
    }
  };

  // 处理文件选择
  const handleFileSelect = (e) => {
    if (e.target.files && e.target.files[0]) {
      handleFiles(e.target.files);
    }
  };

  // 处理文件
  const handleFiles = (files) => {
    setIsUploading(true);
    setUploadProgress(0);
    
    // 模拟文件上传和向量化过程
    const fileArray = Array.from(files);
    setUploadedFiles(prev => [...prev, ...fileArray]);
    
    const interval = setInterval(() => {
      setUploadProgress(prev => {
        if (prev >= 100) {
          clearInterval(interval);
          setIsUploading(false);
          return 100;
        }
        return prev + 10;
      });
    }, 200);
  };

  // 触发文件选择
  const onButtonClick = () => {
    fileInputRef.current.click();
  };

  // 生成访问量图表数据点
  const generateChartPoints = () => {
    return visitData.map((item, index) => ({
      x: (index / (visitData.length - 1)) * 100,
      y: 100 - (item.visits / Math.max(...visitData.map(d => d.visits))) * 100,
      visits: item.visits,
      date: item.date
    }));
  };

  const chartPoints = generateChartPoints();

  return (
    <div className="admin-container">
      {/* 顶部导航栏 */}
      <header className="admin-header">
        <div className="admin-header-content">
          <h1 className="admin-title">重邮AI问答系统 - 管理后台</h1>
          <button className="logout-button" onClick={onLogout}>
            退出登录
          </button>
        </div>
      </header>

      {/* 主要内容区域 */}
      <main className="admin-main">
        <div className="admin-grid">
          {/* 文件上传区域 */}
          <div className="admin-card upload-section">
            <h2 className="card-title">知识库文件上传</h2>
            <div 
              className={`upload-area ${dragActive ? 'drag-active' : ''}`}
              onDragEnter={handleDrag}
              onDragLeave={handleDrag}
              onDragOver={handleDrag}
              onDrop={handleDrop}
              onClick={onButtonClick}
            >
              <div className="upload-content">
                <svg className="upload-icon" fill="currentColor" viewBox="0 0 20 20">
                  <path fillRule="evenodd" d="M3 17a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zM6.293 6.707a1 1 0 010-1.414l3-3a1 1 0 011.414 0l3 3a1 1 0 01-1.414 1.414L11 5.414V13a1 1 0 11-2 0V5.414L7.707 6.707a1 1 0 01-1.414 0z" clipRule="evenodd" />
                </svg>
                <p className="upload-text">拖拽文件到此处或点击上传</p>
                <p className="upload-hint">支持 PDF、TXT、DOC、DOCX 格式</p>
              </div>
              <input
                ref={fileInputRef}
                type="file"
                multiple
                onChange={handleFileSelect}
                accept=".pdf,.txt,.doc,.docx"
                className="file-input"
              />
            </div>

            {/* 上传进度 */}
            {isUploading && (
              <div className="upload-progress">
                <div className="progress-bar">
                  <div 
                    className="progress-fill" 
                    style={{ width: `${uploadProgress}%` }}
                  ></div>
                </div>
                <p className="progress-text">正在处理文件... {uploadProgress}%</p>
              </div>
            )}

            {/* 已上传文件列表 */}
            {uploadedFiles.length > 0 && (
              <div className="uploaded-files">
                <h3>已上传文件</h3>
                <ul className="file-list">
                  {uploadedFiles.map((file, index) => (
                    <li key={index} className="file-item">
                      <span className="file-name">{file.name}</span>
                      <span className="file-size">({(file.size / 1024).toFixed(1)} KB)</span>
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>

          {/* 用户访问记录 */}
          <div className="admin-card user-section">
            <h2 className="card-title">用户访问记录</h2>
            <div className="user-list">
              {userVisits.map(visit => (
                <div key={visit.id} className="user-item">
                  <div className="user-info">
                    <span className="username">{visit.username}</span>
                    <span className="visit-time">{visit.time}</span>
                  </div>
                  <div className="user-question">
                    <span className="question-label">问题：</span>
                    <span className="question-text">{visit.question}</span>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* 访问量统计 */}
          <div className="admin-card stats-section">
            <div className="stats-header">
              <h2 className="card-title">访问量统计</h2>
              <div className="date-selector">
                <select 
                  value={selectedYear} 
                  onChange={(e) => setSelectedYear(parseInt(e.target.value))}
                  className="date-select"
                >
                  <option value={2024}>2024年</option>
                  <option value={2023}>2023年</option>
                </select>
                <select 
                  value={selectedMonth} 
                  onChange={(e) => setSelectedMonth(parseInt(e.target.value))}
                  className="date-select"
                >
                  {Array.from({length: 12}, (_, i) => i + 1).map(month => (
                    <option key={month} value={month}>{month}月</option>
                  ))}
                </select>
              </div>
            </div>
            
            <div className="chart-container">
              <svg className="chart" viewBox="0 0 100 100" preserveAspectRatio="none">
                <defs>
                  <linearGradient id="lineGradient" x1="0%" y1="0%" x2="100%" y2="0%">
                    <stop offset="0%" stopColor="#3b82f6" />
                    <stop offset="100%" stopColor="#1d4ed8" />
                  </linearGradient>
                </defs>
                
                {/* 网格线 */}
                <g className="grid-lines">
                  {[0, 25, 50, 75, 100].map(y => (
                    <line key={y} x1="0" y1={y} x2="100" y2={y} stroke="#e5e7eb" strokeWidth="0.5" />
                  ))}
                </g>
                
                {/* 折线图 */}
                <polyline
                  className="chart-line"
                  points={chartPoints.map(point => `${point.x},${point.y}`).join(' ')}
                  fill="none"
                  stroke="url(#lineGradient)"
                  strokeWidth="2"
                />
                
                {/* 数据点 */}
                {chartPoints.map((point, index) => (
                  <g key={index} className="data-point">
                    <circle
                      cx={point.x}
                      cy={point.y}
                      r="2"
                      fill="#3b82f6"
                      className="point-circle"
                    />
                    <title>{`${point.date}: ${point.visits} 次访问`}</title>
                  </g>
                ))}
              </svg>
              
              {/* 图表标签 */}
              <div className="chart-labels">
                <div className="label-item">
                  <span className="label-text">最高访问量</span>
                  <span className="label-value">{Math.max(...visitData.map(d => d.visits))}</span>
                </div>
                <div className="label-item">
                  <span className="label-text">平均访问量</span>
                  <span className="label-value">
                    {Math.round(visitData.reduce((sum, d) => sum + d.visits, 0) / visitData.length)}
                  </span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default AdminPage;
